$(document).ready(function(){$("body").on("fileActionEvent",function(a,b){if(b.obj.hasClass("hexdump")){ToolBox.getDialogByPost({action:"hexdump",file:b.file},function(c){var d=$(c);$("pre.hexdump",d).css({"max-height":Math.max(Math.floor($(window).height()*0.8),200)+"px",overflow:"auto","padding-right":"2em"});d.dialog({width:"auto",height:"auto",dialogClass:"hexdump",closeText:$("#close").html(),resizeStop:function(){var e=d.height()-$(".hexdump.filename",d).height()-30;$("pre.hexdump",d).css({height:e,"max-height":e})}})})}})});