$(document).ready(function(){$("body").on("fileActionEvent",function(a,b){if(b.obj.hasClass("odfconvert")){ToolBox.hidePopupMenu();var c=window.location.pathname;var e=ToolBox.blockPage();var d=$.post(c,{action:"odfconvert",file:b.file,ct:b.obj.data("ct")},function(f){e.remove();ToolBox.handleJSONResponse(f);if(c==window.location.pathname){ToolBox.updateFileList()}});ToolBox.renderAbortDialog(d)}})});